import React from "react";
import { Link } from "react-router-dom";
import "./Categories.css";
import nature from "../assets/images/nature.jpg";
import travel from "../assets/images/travel.jpg";
import urban from "../assets/images/urban.jpg";

function Categories() {
  const cats = [
    {
      name: "Nature",
      img: nature,
      desc: "Stories from forests, oceans, and the raw beauty of the wild.",
    },
    {
      name: "Travel",
      img: travel,
      desc: "Journey through cities, mountains, and cultures across the globe.",
    },
    {
      name: "Urban",
      img: urban,
      desc: "Life in the fast lane — concrete jungles and modern tales.",
    },
  ];

  return (
    <div className="categories container">
      {cats.map((cat) => (
        <Link key={cat.name} to={`/category/${cat.name}`}>
          <div className="category-card">
            <img src={cat.img} alt={cat.name} />
            <h3>{cat.name}</h3>
            <p>{cat.desc}</p>
          </div>
        </Link>
      ))}
    </div>
  );
}

export default Categories;
